import React, { Fragment } from "react";

//Recibimos de props la propiedad data y el metodo onChange
//Obtenemos de data: id, description y done
//En el atributo del inputt, agregamos el metodo onChange, cuando se 
//da click, va a ejecutar el método.
const TaskItem = props => {
  const {
    onChange,
    data: { id, description, done }
  } = props;
  return (
    
    <Fragment>
     
      <label className="nuevoItem">
        <input
          className="estadoItem"
          name={id}
          type="checkbox"
          defaultChecked={done}
          onChange={onChange}
        />
        <svg
          xmlns="http://www.w3.org/2000/svg"
          xmlnsXlink="http://www.w3.org/1999/xlink"
          viewBox="0 0 200 25"
          className="todo__icon"
        >
          <use xlinkHref="#todo__line" className="todo__line" />
          <use xlinkHref="#todo__box" className="todo__box" />
          <use xlinkHref="#todo__check" className="todo__check" />
          <use xlinkHref="#todo__circle" className="todo__circle" />
        </svg>
        <div className="estadoTexto">{description}</div>
      </label>
    </Fragment>
  );
};

export default TaskItem;
