//da click, va a ejecutar el método.
const TaskItem = props => {
  const {
    onChange,
    data: { id, description, done }
  } = props;
  const changeStyle = () => {
    if (style !== "light") setStyle("light");
    else setStyle("dark");
};
  return (
    <Fragment>
     
      <label className="nuevoItem">
        <input
          className="estadoItem"
          name={id}
          type="checkbox"
          defaultChecked={done}
          onChange={onChange}
          onClick={tachaTexto}
        />
        <div className="estadoTexto">{description}</div>
      </label>
    </Fragment>
     
  );
};

export default TaskItem;
